// https://leetcode.com/problems/find-all-anagrams-in-a-string/description/

package main

import (
	"fmt"
	"time"
)

type input struct {
	s string
	p string
}

func main() {
	// s := "cbaebabacd"
	// p := "abc"

	// input := input{
	// 	s: "",
	// 	p: "",
	// }

	input := input{
		s: "cbaebabacd",
		p: "abc",
	}

	timeStart := time.Now()
	fmt.Println(findAnagrams(input.s, input.p))
	fmt.Println("time used: ", time.Since(timeStart))
}

func findAnagrams(s string, p string) []int {
	res := []int{}
	if len(s) < len(p) {
		return res
	}

	freqMapP := make(map[rune]int)
	freqMapS := make(map[rune]int)
	for i := 0; i < len(p); i++ {
		freqMapP[rune(p[i])]++
		freqMapS[rune(s[i])]++
	}

	for i := len(p); i <= len(s); i++ {
		if isAnagram(freqMapP, freqMapS) {
			res = append(res, i-len(p))
		}

		inRange := i < len(s)
		if inRange {
			newChar := rune(s[i])
			outgoingChar := rune(s[i-len(p)])

			// for real ._. i didnt really know that i can slide window in this way
			freqMapS[newChar]++
			freqMapS[outgoingChar]--

			if freqMapS[outgoingChar] == 0 {
				delete(freqMapS, outgoingChar)
			}
		}
	}

	return res
}

func isAnagram(map1, map2 map[rune]int) bool {
	for key, val := range map1 {
		if map2[key] != val {
			return false
		}
	}

	return true
}
