// https://leetcode.com/problems/find-all-anagrams-in-a-string/description/

package main

import (
	"fmt"
	"time"
)

type input struct {
	s string
	p string
}

func main() {
	// s := "cbaebabacd"
	// p := "abc"

	input := input{
		s: "",
		p: "",
	}

	timeStart := time.Now()
	fmt.Println(findAnagrams(input.s, input.p))
	fmt.Println("time used: ", time.Since(timeStart))
}

func findAnagrams(s string, p string) []int {
	left := 0
	right := len(p)
	pMap := cntCharFreq(p)

	res := []int{}
	for right <= len(s) {
		ssMap := cntCharFreq(s[left:right])

		if isAnagram(pMap, ssMap) {
			res = append(res, left)
		}

		left++
		right++
	}

	return res
}

func cntCharFreq(subStr string) map[rune]int {
	charFreqMap := make(map[rune]int)
	for _, char := range subStr {
		charFreqMap[char]++
	}

	return charFreqMap
}

func isAnagram(pMap, ssMap map[rune]int) bool {
	for key, val := range pMap {
		if !(ssMap[key] == val) {
			return false
		}
	}

	return true
}
